# python tests/decode.py

import bz2
import base64

# The compressed and encoded string (example value, replace with actual compressed_string)
compressed_string = ""

# Step 1: Base64 decode the compressed string
compressed_data = base64.b64decode(compressed_string.encode("utf-8"))

# Step 2: Decompress the data using BZ2
decompressor = bz2.BZ2Decompressor()
decompressed_data = decompressor.decompress(compressed_data)

# Convert bytes back to the original string
original_string = decompressed_data.decode("utf-8")

with open("tests/8.txt", "w") as f:
    f.write(original_string)
